import React, { useState } from "react";

const Contform = () => {

  const [data,setData] = useState({
    fullname : "",
    phone : "",
    email : "",
    message : ""
  })

  const InputEvent = (event) => {
    const {name,value} =event.target;

    setData ((preval) => {
      return {
        ...preval,
        [name] : value,
      };
    });
  };

  const formSubmit = (e) =>{
    e.preventDefault();
    alert(` My Name is ${data.fullname} \n My phone number ${data.phone} \n My email address ${data.email} \n My Message are -: ${data.message}`)
  }

  return (
    <>
      {/* <div className="my-5"> */}
        {/* <div className="text-center fs-1 fw-semibold">Let's make somthing great !</div> */}
        {/* <h1>Let's make somthing great !</h1>
      </div>
      <div className="container contact-div">
        <div className="row">
          <div className="col-md-6 col-12 mx-auto"> */}

            <from >

            <div className="input-group has-validation">
                <div className="form-floating is-invalid mb-4">
                    <input
                      type="text"
                      className="form-control"
                      id="floatingInputGroup2"
                      name="fullname"
                      value={data.fullname}
                      onChange={InputEvent}
                      placeholder="Username"
                    />
                    <label for="floatingInputGroup2">Enter Full Name</label>
                </div>
            </div>

            <div className="input-group ">
                <span className="input-group-text">+91</span>
                <div className="form-floating">
                    <input
                      type="number"
                      className="form-control "
                      id="floatingInputGroup2"
                      name="phone"
                      value={data.phone}
                      onChange={InputEvent}
                      placeholder="number"
                      required/>
                    <label for="floatingInputGroup2">Mobile Number</label>
                </div>
            </div>

            <div className="input-group my-4">
                <span className="input-group-text">@</span>
                <div className="form-floating is-invalid">
                    <input
                      type="email"
                      className="form-control"
                      id="floatingInputGroup2"
                      name="email"
                      value={data.email}
                      onChange={InputEvent}
                      placeholder="Username"
                      required
                      />
                    <label for="floatingInputGroup2">Enter Email address</label>
                </div>
            </div>

            <div className="input-group has-validation">
            <div className="form-floating">
                <textarea
                  className="form-control"
                  rows='3'
                  name="message"
                  value={data.message}
                  onChange={InputEvent}
                  placeholder="Leave a comment here"
                  id="floatingTextarea2"/>
                <label for="floatingTextarea2">Message</label>
            </div>
            </div>

            <div className="col-12 my-4">
                <button className="btn btn-outline-primary" type="submit" onClick={formSubmit}>
                    Submit Form
                </button>
            </div>
            </from>
          {/* </div>
        </div>
      </div> */}
    </>
  );
};

export default Contform;
